using namespace std;

void print(int a[], int n) {

    for (int i = 0; i < n; i++) {
        cout << a[i] << " ";
    }
    cout << endl;
}

void print(vector<int> a) {//
    int n = a.size();
    for (int i = 0; i < n; i++) {
        cout << a[i] << " ";
    }
    cout << endl;
}
int max(int a,int b)
{
    if(a>b)
    {
        return a;
    }
    return b;
}
int32_t main() {
    string s="";
    string p="yqrbgjdwtcaxzsnifvhmou";
    vector<int> ans;
    vector<int> v(26,0);
    for(int i=0;i<p.size();i++)
    {
        v[p[i]-'a']++;
    }
    print(v);
    int range=max(0,s.size()-p.size()+1);
    for(int i=0;i<range;i++)//
    {
        string tem=s.substr(i,p.size());
        vector<int> term(26,0);
        for(int j=0;j<tem.size();j++)
        {
            term[tem[j]-'a']++;
        }
        if(term==v)
        {
            ans.push_back(i);
        }
    }
   cout<<ans.size();

}
